<?phpclass Admin_model extends CI_Model {    private $table = 'user';    const LOGIN_FAIL = -1;    const ROLE_MEM = 0;    const ROLE_ADMIN = 9;    const ROLE_POST_PIC = 1;    const ROLE_POST_NEWS = 2;    const ROLE_POST = 3; /* 2 + 1 */    public function __construct() {        $this->load->database();    }    public function login($username, $password) {        $username = $this->db->escape_str($username);        $password = $this->db->escape_str($password);        $this->db->select('role')->where(array(            'username' => $username,            'password' => $password));        $query = $this->db->get($this->table);        $ret = $query->row();        return (empty($ret) ? Admin_model::LOGIN_FAIL : (int) $ret->role);    }    public function getRoleName($role) {        if ($role === Admin_model::ROLE_ADMIN)            return 'admin';        if ($role === Admin_model::ROLE_POST_PIC)            return 'pic';        if ($role === Admin_model::ROLE_POST_NEWS)            return 'news';        if ($role === Admin_model::ROLE_POST)            return 'post';    }}