<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Admin extends MY_Admin_Controller {    function __construct() {        parent::__construct();        $login = $this->session->userdata('admin');        $method = $this->router->fetch_method();        if (!$login) {            redirect('admin/filter');            exit;        }        redirect('admin/product/category/0');        exit;    }    public function index() {        $this->load->view('admin/main');    }// MENU // ORDER// LANDSCAPE// BLOG    // CALLBACK     function date_callback($post_array) {        if (empty($post_array['time'])) {            $post_array['time'] = date("Y-m-d");        }        return $post_array;    }    // CALLBACK COLUMNS}