<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Filter extends CI_Controller {    function index() {        //  $this->output->cache(99999);        $login = $this->session->userdata('admin');        if ($login) {            return redirect('admin/admin');        }        $this->load->view('admin/loginform');    }    function submit() {        $this->load->library('form_validation');        $this->form_validation->set_rules('username', 'Tên đăng nhập', 'required');        $this->form_validation->set_rules('password', 'Mật khẩu', 'required');        if ($this->form_validation->run() === FALSE) {            redirect('admin/filter');        } else {            $this->load->model('admin_model');            $ret = $this->admin_model->login($this->input->post('username'), $this->input->post('password'));            if ($ret === -1) {                redirect('admin/filter');            } else {                $this->session->set_userdata('admin', $ret);                redirect('admin/admin');            }        }    }    function logout() {        $this->session->unset_userdata('admin');        redirect('admin/filter');    }}