<?php if (!defined('BASEPATH'))    exit('No direct script access allowed');class Adminlogin extends CI_Controller {    const table = 'audio';    function __construct() {        parent::__construct();    }    function index() {        $this->output->cache(99999);        $this->load->view('admin/loginform');    }    function login() {        $this->load->library('form_validation');        $this->form_validation->set_rules('username', 'Tên đăng nhập', 'required');        $this->form_validation->set_rules('password', 'Mật khẩu', 'required');        if ($this->form_validation->run() === FALSE) {            redirect('adminlogin');        } else {            $this->load->model('admin_model');            $ret = $this->admin_model->login($this->input->post('username'), $this->input->post('password'));            if ($ret === -1) { // ko thành công                redirect('adminlogin');            } else {                session_start();                $_SESSION['admin'] = $ret;                redirect('admin');            }        }    }    function logout() {        session_start();        $_SESSION['admin'] = $ret;        redirect('adminlogin');    }}?>