<?phpclass Category extends CI_Controller {    public function __construct() {        parent::__construct();//        $this->output->cache(60 * 3);        $this->load->model('categoryModel');        $this->load->model('productModel');    }    public function view($slug, $page = 1) {        $page = (int) $page;        $category = $this->categoryModel->getBySlug($slug);        if (empty($category)) {            show_404();            return;        }        $this->title = $category['name'] . ' - ITshop24h';        $data['page_num'] = $this->categoryModel->getPageNum($category);        $data['products'] = $this->categoryModel->getPageProducts($category, $page);        $category['slug'] = $slug;        $data['category'] = $category;        $this->load->view('category', $data);    }}?>