<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Main extends CI_Controller {    function __construct() {        parent::__construct();        $this->load->model('productModel');    }    function index() {        $data['categories'] = $this->categoryModel->getAll();     //   $data['products'] = $this->productModel->getNew();        $this->load->view('home', $data);    }    function quotation() {        $this->title = 'Bảng giá của ITshop24h';        $this->load->helper('directory');        $data['map'] = directory_map('./assets/quotation/');        $this->load->view('quotation', $data);    }    function download() {        $this->title = 'Các phần mềm tiện ích được ITshop24h giới thiệu';        $this->load->view('download');    }    function contact() {        $this->title = 'Liên hệ ITshop24h';        $this->load->view('contact');    }}