<aside id="sidebar" class="col-md-4">    <?php $controller = $this->router->fetch_class(); ?>    <?php if ($controller !== 'article'): ?>        <!-- SAN PHAM MOI -->        <div class="widget">            <div class="widget-heading">                <h2>Sản phẩm bán chạy</h2>            </div>            <div class="widget-body">                <div class="list-hot-product">                    <?php $hot_products = $this->productModel->getHot(); ?>                    <?php foreach ($hot_products as $product): ?>                        <div class="media">                            <a class="pull-left" href="<?= site_url() , 'san-pham/' , $product['slug'] ?>.html">                                <img class="media-object" src="<?= site_url() ?>assets/uploads/product/<?= $product['img'] ?>" alt="<?= $product['name'] ?>">                            </a>                            <div class="media-body">                                <h4 class="media-heading"><a href="<?= site_url() , 'san-pham/' , $product['slug']  ?>.html"><?= $product['name'] ?></a></h4>                                <div class="product-price">                                    <strong><span class="glyphicon glyphicon-tag"></span> Giá: </strong> <?= number_format($product['price']) ?> VND                                </div>                                <div class="product-link">                                    <button class="btn-link product-cart" data-slug="<?= $product['slug']?>"><span class="glyphicon glyphicon-shopping-cart"></span>Đặt mua</button>                                    <a class="product-detail" href="<?=site_url() , 'san-pham/' , $product['slug']  ?>.html"><span class="glyphicon glyphicon-play"></span>Chi tiết</a>                                </div>                                </div>                        </div>                    <?php endforeach; ?>                </div>            </div>        </div>    <?php elseif ($this->router->fetch_method() === 'view'): ?>            <!-- BAI VIET KHAC -->        <div class="widget">            <div class="widget-heading">                <h2><?= $this->is_service ? 'Dịch vụ ' : 'Bài viết '?> khác</h2>            </div>            <div class="widget-body">                <div class="list-hot-product">                    <?php foreach ($articles as $item): ?>                        <?php                        if (empty($item['img'])) {                            $item['img'] = 'nophoto.jpg';                        }                        $w = $this->is_service ? 'dich-vu/' : 'thu-thuat/';                        ?>                        <div class="media">                            <a class="pull-left" href="<?= site_url(), $w ,$item['slug'] ?>.html">                                <img class="media-object" src="<?= site_url() ?>assets/uploads/article/<?= $item['img'] ?>" alt="<?= $item['title'] ?>">                            </a>                            <div class="media-body">                                <h4 class="media-heading"><a href="<?= site_url(), $w ,$item['slug'] ?>.html"><?= $item['title'] ?></a></h4>                                <p><?= $item['describe'] ?></p>                             </div>                        </div>                    <?php endforeach; ?>                </div>            </div>        </div>    <?php endif; ?>        <?php if ($controller !== 'cart'): ?>           <!-- LOC SAN PHAM -->        <div class="widget">            <div class="widget-heading">                <h2> Lọc sản phẩm theo giá</h2>            </div>            <div class="widget-body">                <form id="filter_form" action="<?= site_url() . 'product/filter/' ?>">                    <div class="form-group">                        <select name="cat_id" class="form-control">                            <option value="0">--- Chọn danh mục</option>                            <?php $categories = $this->categoryModel->getAll(); ?>                            <?php foreach ($categories as $cat) : ?>                                <option value="<?= $cat['id'] ?>">---<?= $cat['name'] ?></option>                                <?php if (!empty($cat['child'])) : ?>                                    <?php foreach ($cat['child'] as $child) : ?>                                        <option value="<?= $child['id'] ?>">------ <?= $child['name'] ?></option>                                    <?php endforeach; ?>                                <?php endif; ?>                            <?php endforeach; ?>                        </select>                    </div>                    <div class="form-group">                        <input  class="form-control" name="min" required  type="number" step="50000"  min="0" placeholder="-- Giá từ --" />                    </div>					                    <div class="form-group">                        <input  class="form-control" name="max" required type="number" step="50000" min="50000" placeholder="-- Giá đến --" />                    </div>		                    <div>                        <button class="btn btn-primary">Tìm kiếm</button>                    </div>                </form>		            </div>        </div>    <?php endif; ?></aside>