<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Admin extends CI_Controller {    function __construct() {        parent::__construct();        $login = $this->session->userdata('admin');        if (!$login) {            return redirect('admin/filter');        }    }    public function index() {        $this->load->library('grocery_CRUD');        $this->crud = new grocery_CRUD();        $this->crud->set_theme('twitter-bootstrap');        $this->crud->unset_print();        $this->load->view('admin/main');    }            // CALLBACK COLUMNS}?>