<?phpclass Admin_model extends CI_Model {    private $table = 'user';    public function __construct() {        $this->load->database();    }    public function login($username, $password) {        $username = $this->db->escape_str($username);        $password = $this->db->escape_str($password);        $this->db->select('id')->where(array(            'username' => $username,            'password' => $password));        $query = $this->db->get($this->table);        $ret = $query->row();        return (empty($ret) ? -1 : (int) $ret->id);    }}