<?phpfunction getVar($name, $type='get',$default = ''){	if($type=='get'){		return isset($_GET[$name]) ? $_GET[$name] : $default;	}else if($type=='post'){		return isset($_POST[$name]) ? $_POST[$name] : $default;	}else if($type=='request'){		return isset($_REQUEST[$name]) ? $_REQUEST[$name] : $default;		}else if($type=='session'){		return isset($_SESSION[$name]) ? $_SESSION[$name] : $default;		}else if($type=='cookie'){		return isset($_COOKIE[$name]) ? $_COOKIE[$name] : $default;		}	}