<?php/* CONTROL */if(isset($_POST['submit'])){	$title = $_POST['title'];	$sum = $_POST['sum'];	$content = $_POST['content'];	$hot = (int)$_POST['hot'];	$img = strtolower($_FILES['img']['name']);	$cat_id = (int)$_POST['category_id'];	$subcat_id = (int)$_POST['subcat_id'];	if(!empty($title) && !empty($sum) && !empty($content) &&!empty($cat_id)){		include '../includes/common.php';			$atitle = khongdau($title);		if(!empty($img)){  /********** check file hinh khi co upload */			$allowed =  array('jpg','jpeg','png','gif');			$ext = pathinfo($img, PATHINFO_EXTENSION);			if(!in_array($ext,$allowed)){				echo '<script>alert("Không cho phép up file hình này")</script>';			}else{				$path = '../images/news/';				$filename= $path.$img;				$a =1;				while (file_exists($filename)) {					$filename=$path.$a.$img;					++$a;				}				if(move_uploaded_file($_FILES['img']["tmp_name"],$filename)){ /* UPLOAD HINH + THEM VAO CSDL */					$kq = $news->addNews($title,$sum,$content,$img,$cat_id, $subcat_id,$atitle,$hot);				}else{					echo '<script>alert("Upload thất bại")</script>';				}				}		}else if($hot==0){ /********** khong upload hinh */			$img = '';			$kq = $news->addNews($title,$sum,$content,$img,$cat_id,$subcat_id,$atitle,$hot);		}else{			echo '<script>alert("Không có hình không thể thành bài viết nóng")</script>';		}		if(isset($kq)){			echo '<script>alert("Đăng bài thành công")</script>';		}		}else{		echo '<script>alert("Nhập thiếu thông tin")</script>';	}}/* VIEW  */include 'view/newsform.php';?>