<?phpclass RSS{	private $conn;	public function RSS(){		$this->conn = new mysqli(DB_HOST,DB_USER,DB_PASSWORD,DB_DATABASE);			$this->conn->set_charset('utf8');			}		public function GetFeed(){		return $this->getDetails() . $this->getItems();	}			private function getDetails(){		$details = '<?xml version="1.0" encoding="utf-8" ?>				<rss version="2.0">					<channel>						<title> Hot News </title>						<link>'.$GLOBALS['url_host'].'rss/</link>						<description> Bài mới nhất từ tuoitrau hehe </description>						<language> Vietnamese </language>';		return $details;	}		private function getItems()	{		$itemsTable = "news";		$query = "SELECT atitle, title, summary FROM ". $itemsTable.' LIMIT 10';		$result = $this->conn->query($query);		$items = '';		while($row = $result->fetch_assoc())		{			$items .= '<item>						 <title>'. $row["title"] .'</title>						 <link>'.$GLOBALS['url_host'].'chi-tiet-tin/'.$row["atitle"].'.html</link>						 <description><![CDATA['. $row["summary"] .']]></description>					 </item>';		}		$items .= '</channel>				 </rss>';		return $items;	}}?>