INSERT INTO `news` (`atitle`, `news_id`, `title`, `summary`, `content`, `image`, `date`, `subcategory_id`, `category_id`, `views`, `hot`, `like`, `comments`) VALUES
('Truong-chuyen-Tran-Dai-Nghia-tuyen-320-hoc-sinh-lop-6', 13, 'Trường chuyên Trần Đại Nghĩa tuyển 320 học sinh lớp 6', 'Sở GD-ĐT TP.HCM vừa có thông báo sẽ tuyển 320 học sinh lớp 6 (năm học 2013-2014) cho Trường THPT chuyên Trần Đại Nghĩa!', '<div style="outline-style: none; color: rgb(51, 51, 51); font-family: Arial; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255); position: relative; top: -15px;"><p class="pBody" style="outline-style: none; font-family: arial; color: rgb(0, 0, 0);">Theo đó, học sinh đã hoàn thành chương trình cấp tiểu học tại TP.HCM và có học lực môn của năm học lớp 5 hai môn tiếng Việt và toán đạt loại giỏi sẽ được đăng ký dự thi.</p><p class="pBody" style="outline-style: none; font-family: arial; color: rgb(0, 0, 0);">Thí sinh dự thi 3 môn: tiếng Việt, tiếng Anh và toán trong 2 ngày 29 và 30-6-2013 theo lịch sau:</p><div align="center" style="outline-style: none;"><table class="MsoNormalTable" border="1" cellspacing="0" cellpadding="0" style="outline-style: none; border: medium none; border-collapse: collapse;"><tbody style="outline-style: none;"><tr style="outline-style: none; height: 69.45pt;"><td valign="top" width="85" style="outline-style: none; border: 1pt solid windowtext; padding: 0in 5.4pt; background-color: transparent; width: 64pt; height: 69.45pt;"><p class="MsoNormal" align="center" style="outline-style: none; text-align: center; margin: 6pt 0in 0pt;"><span style="outline-style: none; font-size: 10pt; font-weight: bold;">Ngày<o:p style="outline-style: none;"></o:p></span></p></td><td valign="top" width="60" style="outline-style: none; border-color: windowtext windowtext windowtext rgb(236, 233, 216); border-bottom-width: 1pt; border-bottom-style: solid; padding: 0in 5.4pt; background-color: transparent; width: 45pt; height: 69.45pt; border-top-width: 1pt; border-top-style: solid; border-right-width: 1pt; border-right-style: solid;"><p class="MsoNormal" align="center" style="outline-style: none; text-align: center; margin: 6pt 0in 0pt;"><span style="outline-style: none; font-size: 10pt; font-weight: bold;">Buổi<o:p style="outline-style: none;"></o:p></span></p></td><td valign="top" width="78" style="outline-style: none; border-color: windowtext windowtext rgb(236, 233, 216) rgb(236, 233, 216); padding: 0in 5.4pt; background-color: transparent; width: 58.4pt; height: 69.45pt; border-top-width: 1pt; border-top-style: solid; border-right-width: 1pt; border-right-style: solid;"><h6 style="outline-style: none; font-weight: normal; padding: 0px; margin: 6pt 0px 0px; font-size: 10px;"><span style="outline-style: none; font-size: 10pt;">Môn thi<o:p style="outline-style: none;"></o:p></span></h6></td><td valign="top" width="76" style="outline-style: none; border-color: windowtext windowtext windowtext rgb(236, 233, 216); border-bottom-width: 1pt; border-bottom-style: solid; padding: 0in 5.4pt; background-color: transparent; width: 56.95pt; height: 69.45pt; border-top-width: 1pt; border-top-style: solid; border-right-width: 1pt; border-right-style: solid;"><p class="MsoNormal" align="center" style="outline-style: none; text-align: center; margin: 6pt 0in 0pt;"><span style="outline-style: none; font-size: 10pt; font-weight: bold;">Thời gian làm bài<o:p style="outline-style: none;"></o:p></span></p></td><td valign="top" width="95" style="outline-style: none; border-color: windowtext windowtext windowtext rgb(236, 233, 216); border-bottom-width: 1pt; border-bottom-style: solid; padding: 0in 5.4pt; background-color: transparent; width: 70.95pt; height: 69.45pt; border-top-width: 1pt; border-top-style: solid; border-right-width: 1pt; border-right-style: solid;"><p class="MsoNormal" align="center" style="outline-style: none; text-align: center; margin: 6pt 0in 0pt;"><span style="outline-style: none; font-size: 10pt; font-weight: bold;">Giờ thí sinh có mặt<o:p style="outline-style: none;"></o:p></span></p></td><td valign="top" width="91" style="outline-style: none; border-color: windowt